function visualize(static_file, dynamic_file, results_file, number_of_cells, particle_focus)  sfid = fopen(static_file, 'r');  N = str2num(fgetl(sfid)); # Number of particles  L = str2num(fgetl(sfid)); # Size of area  fclose(sfid);    # Getting the properties from the static file, skipping 2 header lines  [radius colour] = textread(static_file,"%f %f", 'headerLines', 2);    # Getting the positions and velocities from the dynamic file, skipping 1 header line  [x y vx vy] = textread(dynamic_file,"%f %f %f %f", 'headerLines', 1);    ofid = fopen(results_file, 'r');    # Empty cell array to store the neighbours  neighbour = {};  for i = 1:N    # Get the line and add it to the neighbours of the cell    line = fgetl(ofid);    neighbour{i} = textscan(line,'%d');  end    # Creating the plot with the data  plot = scatter(x,y,'b');  title('Index Cell Method');  xlabel('X position');  ylabel('Y position');  hold on;    # Creating the plot for the particle in focus  particle_x = x(particle_focus);  particle_y = y(particle_focus);  plot = scatter(particle_x,particle_y,'r','filled');    # Creating the plot for the neighbours  neighbours = neighbour{particle_focus}{1}(2:end);  neighbours_x = x(neighbours);  neighbours_y = y(neighbours);  plot = scatter(neighbours_x,neighbours_y,'g','filled');    # Adding the tick marks  set(gca,'xtick',[0:(L/number_of_cells):L]);  set(gca,'ytick',[0:(L/number_of_cells):L]);  grid on;  hold off;endfunction
